import classes from './Portfolio.module.css';
import React from 'react';
import Card from '../UI/Card';
import PortfolioItem from './PortfolioItem';

const DUMMYDATA = [
    { id: 1, name: "Apple", amount: '5' },
    { id: 2, name: "Microsoft", amount: '10' }
]

function Portfolio() {
    const stockList = DUMMYDATA.map(stock => 
        <PortfolioItem
        id={stock.id}
        stockName = {stock.name}
        amount = {stock.amount}
        />
    );


    return (
        <Card>
            <table>
                <tr>
                    <th className={classes.header}>Stock Name</th>
                    <th className={classes.header}>Amount</th>
                </tr>
                <tr>
                    <tr>
                        <td className={classes.stock}>{stockList.stockName}</td>
                        <td className={classes.amount}>{stockList.amount}</td>

                    </tr>
                </tr>

            </table>

        </Card>

    )
};

export default Portfolio;
