import classes from './PortfolioItem.module.css';
import Button from '../UI/Button';
import { Fragment } from 'react/cjs/react.production.min';

function PortfolioItem(props) {
    async function onDeleteStockHandler() {
        const response = await fetch('http://127.0.0.1:8000/stock-delete/' + props.id, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            }
        })
    }


    return (
        <Fragment>

            <tr>
                <td className={classes.td}>{props.name}</td>
                <td className={classes.td_amount}>{props.amount}</td>
                <td className={classes.td}> ${props.mark}</td>

            </tr>
            <Button onClick={onDeleteStockHandler}>DELETE</Button>
        </Fragment>
    )
}

export default PortfolioItem;
