import { React, Fragment, useState, useEffect } from 'react';
import Card from '../UI/Card';
import PortfolioItem from './PortfolioItem';
import Modal from '../UI/Modal';
import PortfolioAdd from './PortfolioAdd';
import Button from '../UI/Button';
import PortfolioHeader from './PortfolioHeader'



// const DUMMYDATA = [
//     { id: 1, name: "Apple", amount: 5, mark: 150 },
//     { id: 2, name: "Microsoft", amount: 10, mark: 210 }
// ]

function Portfolio(props) {
    const [stockLists, setStockLists] = useState([])

    const onSaveStockToListHandler = (stockData) => {
        console.log(stockData)
        setStockLists((prevStockList) => {
            return [stockData, ...prevStockList];

        });
    };
    const [showModal, setShowModal] = useState();

    const showModalHandler = (props) => {
        setShowModal(true)
    }
    const hideModalHandler = (props) => {
        setShowModal(false)
    }
    const ProcessedStockLists = stockLists.map(stock =>
        <PortfolioItem
            key={stock.id}
            id={stock.id}
            name={stock.name}
            amount={stock.amount}
            mark={stock.mark}
        />
    );

    useEffect(() => {
        setShowModal(false)

    }, [stockLists]);



    return (
        <Fragment>
            <Card>
                <table>
                    <tbody>
                        <PortfolioHeader></PortfolioHeader>
                        {ProcessedStockLists}
                    </tbody>
                </table>
                <Button onClick={showModalHandler}>ADD</Button>
                {showModal && <Modal><PortfolioAdd onClose={hideModalHandler} onSaveStockToList={onSaveStockToListHandler}></PortfolioAdd></Modal>}

            </Card>
        </Fragment>


    )
};

export default Portfolio;
