import classes from './Portfolio.module.css';
import { React, Fragment, useState } from 'react';
import Card from '../UI/Card';
import PortfolioItem from './PortfolioItem';
import PortfolioHeader from './PortfolioHeader';

const DUMMYDATA = [
    { id: 1, name: "Apple", amount: 5, mark: 150 },
    { id: 2, name: "Microsoft", amount: 10, mark: 210 }
]

function Portfolio(props) {
    const [showModal, setShowModal] = useState();

    const showModalHandler = (props) =>{
        setShowModal(true)
    }
    const hideModalHandler = (props) =>{
        setShowModal(false)
    }
    const stockList = DUMMYDATA.map(stock =>
        <PortfolioItem
            id={stock.id}
            name={stock.name}
            amount={stock.amount}
            mark={stock.mark}
        />
    );


    return (
        <Fragment>
            <Card>

                <PortfolioHeader />
                {stockList}
            </Card>
            <Card>
                {showModal && <button onClick={setShowModal}>ADD</button>}
            </Card>
        </Fragment>


    )
};

export default Portfolio;
