import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';
import { React } from 'react';

function StockChart(props) {
    const stockLists = props.stockLists
    let dataList = []
    let labelList = []
    stockLists.map(stock => {
        const stockValue = stock.props.amount * stock.props.mark
        dataList.push(stockValue)
        labelList.push(stock.props.name)

    })

    ChartJS.register(ArcElement, Tooltip, Legend);
    const data = {
        labels: labelList,
        datasets: [
            {
                label: 'stocks',
                data: dataList,
                backgroundColor: [
                    'rgba(255, 99, 132, 0.2)',
                    'rgba(54, 162, 235, 0.2)',
                    'rgba(255, 206, 86, 0.2)',
                    'rgba(75, 192, 192, 0.2)',
                    'rgba(153, 102, 255, 0.2)',
                    'rgba(255, 159, 64, 0.2)',
                ],
                borderColor: [
                    'rgba(255, 99, 132, 1)',
                    'rgba(54, 162, 235, 1)',
                    'rgba(255, 206, 86, 1)',
                    'rgba(75, 192, 192, 1)',
                    'rgba(153, 102, 255, 1)',
                    'rgba(255, 159, 64, 1)',
                ],
                borderWidth: 1,
            },
        ],
    };

    const options = {
        plugins: {
            legend: {
                display: true
            }
        },
        maintainAspectRatio: false,
        title: {
            text: 'TEST',
        }
    }
    return (
        <Pie
            data={data}
            width={400}
            height={400}
            options={options}
        />
    )
}

export default StockChart
