import classes from './PortfolioItem.module.css';
import { Fragment, useContext, useEffect } from 'react';
import ButtonDelete from '../UI/ButtonDelete';
import FetchContext from '../../Store/FetchContext';


 function PortfolioItem(props) {
    const ctx = useContext(FetchContext);

    async function onDeleteStockHandler() {
        await fetch('http://127.0.0.1:8000/stock-delete/' + props.id, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            }
        })
        console.log('deleted!')
        //window.location.reload();
    }

    useEffect(() => {
        ctx.onFetchStockList();
    }, [onDeleteStockHandler]);

    return (
        <Fragment>

            <tr>
                <td className={classes.td}>{props.name} </td>
                <td className={classes.td_amount}>{props.amount}</td>
                <td className={classes.td}> ${props.mark}</td>
                <td><ButtonDelete onClick={ () => { onDeleteStockHandler(); ctx.onFetchStockList();} }>DELETE</ButtonDelete></td>
            </tr>

        </Fragment>
    )
}

export default PortfolioItem;
